import org.junit.jupiter.api.BeforeEach;import org.junit.jupiter.api.Test;import java.io.ByteArrayOutputStream;import java.io.IOException;import java.io.PrintStream;import java.nio.file.Files;import java.nio.file.Path;import static org.junit.jupiter.api.Assertions.*;public class StatisticsTest {    QuestionStatistics statistics = new QuestionStatistics();    @BeforeEach    public void init() {        statistics.questionPassed = new boolean[]{false, false, false, false};        statistics.questionsAttempts = new int[]{0, 0, 0, 0};    }    @Test    public void testPrintStats() {        statistics.CountOfPassedQuestions = 2;        statistics.CountOfAttemptedQuestions = 3;        ByteArrayOutputStream outputStreamCaptor = new ByteArrayOutputStream();        System.setOut(new PrintStream(outputStreamCaptor));        statistics.printStats();        String expectedOutput = "Количество пройденных вопросов: 2/3\r\n" +                "Количество встретившихся вопросов: 3/3\r\n";        assertEquals(expectedOutput, outputStreamCaptor.toString());    }    @Test    public void testUpdateStatsRightAnswer() {        statistics.CountOfPassedQuestions = 0;        statistics.CountOfAttemptedQuestions = 0;        boolean rightAnswer = true;        int numberOfQuestion = 1;        statistics.updateStats(1, rightAnswer);        assertTrue(statistics.questionPassed[numberOfQuestion]);        assertEquals(statistics.CountOfAttemptedQuestions, 1);        assertEquals(statistics.CountOfPassedQuestions, 1);        assertEquals(statistics.questionsAttempts[numberOfQuestion], 1);    }    @Test    public void testUpdateStatsWrongAnswer() {        statistics.CountOfPassedQuestions = 0;        statistics.CountOfAttemptedQuestions = 0;        boolean wrongAnswer = false;        int numberOfQuestion = 1;        statistics.updateStats(1, wrongAnswer);        assertFalse(statistics.questionPassed[numberOfQuestion]);        assertEquals(statistics.CountOfAttemptedQuestions, 1);        assertEquals(statistics.CountOfPassedQuestions, 0);        assertEquals(statistics.questionsAttempts[numberOfQuestion], 1);    }    @Test    public void testSaveStats() {        String testPath = "test.json";        statistics.saveStats(testPath);        assertTrue(Files.exists(Path.of(testPath)));        try {            Files.delete(Path.of(testPath));        } catch (IOException e) {            throw new RuntimeException(e);        }    }    @Test    public void testUploadStats(){    }}